import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import './index.css';

import { library } from '@fortawesome/fontawesome-svg-core';
import { fas } from '@fortawesome/free-solid-svg-icons';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons';

library.add(fas);


export default function After(props) {
    const name = props.name;
    const id = props.id;
    const hassearched = props.hassearched;

    const [messages, setmessages] = useState([]);
    const [inputvalue, setinputvalue] = useState('');

    useEffect(() => {
        socket.emit('user-joined', name);
        socket.on('new-user-joined', (data) => {
            append_message(data, 'left');
        });
        socket.on('message-received', (data) => {
            append_message(data, 'left');
        });

        return () => {
            socket.off('new-user-joined');
            socket.off('message-received');
        };
    }, [name]);

    function handle_send_message() {
      
        if (inputvalue.trim() !== '') {
            let message = inputvalue.trim();
            setmessages((prevmessages) => [...prevmessages, { message: `You: ${message}`, position: 'right' }]);
            socket.emit('message', message);
            setinputvalue('');
        }
    }

    function append_message(msg, position) {
        const newmessages = { message: msg, position: position };
        setmessages((prevmessages) => [...prevmessages, newmessages]);
    }

    function x(e) {
       
        if (e.key === 'Enter') {
          e.preventDefault();
            handle_send_message();
        }
    }

    function y(e){
      e.preventDefault();
      handle_send_message();
    }

    if (!hassearched) {
        return null;
    }

    return (
        <>
            <div className="after">
                {messages.map((value, index) => (
                    <div className={`message ${value.position}`} key={index}>
                        <h2>{value.message}</h2>
                    </div>
                ))}
            </div>
            <div className="message-box">
                <input
                    type="text"
                    className="input-2"
                    placeholder="Enter your message"
                    value={inputvalue}
                    onChange={(e) => setinputvalue(e.target.value)}
                    onKeyDown={x}
                />
                <FontAwesomeIcon icon={faPaperPlane} onClick={y} />
            </div>
        </>
    );
}
