import io from 'socket.io-client';
import React from 'react';
import ReactDom from 'react-dom';
import './index.css';
import {useState,useEffect} from 'react';






import { library } from '@fortawesome/fontawesome-svg-core';
import { fas } from '@fortawesome/free-solid-svg-icons';


import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons';

library.add(fas);
const socket=io('http://localhost:3000/');


export default function After(props){
  
    const name=props.name;
    const id=props.id;
    const hassearched=props.hassearched;
    if(!hassearched){
    return null;
    }

    const [messages,setmessages]= useState([]);
    const [inputvalue,setinputvalue]=new useState('');
    useEffect(()=>{
       socket.emit('user-joined',name);
       socket.on('new-user-joined',(data)=>{
       
        append_message(data,'left');
       })
       socket.on('message-received',(data)=>{
        append_message(data,'left');
       })
       
    },[])

    function handle_send_message(msg){
       if(inputvalue.trim()!=''){
        let message=inputvalue.trim();
        setmessages((prevmessages)=>{
          return [...prevmessages,message];
        })
        setinputvalue('');
       }
    }

    function append_message(msg,position){
      const newmessages={message:msg,position:position};
      setmessages((prevmessages)=>{
    return [...prevmessages,newmessages];
      })

    }

    function x(e){
      e.preventdefault();
      if(e.key=='Enter'){
        handle_send_message(e.target.value);
      }
    }

   
    
return (
    <>
        <div className="after">
           {
            setmessages.map((value,index)=>(
              <div className={`message ${value.position}`}>
              <h2>{value.message}</h2>
              </div>
              )
            )
           }

           
        </div>
        <div className="message-box">
          <input type="text" className="input-2" placeholder="Enter your message" value={inputvalue} onChange={(e)=>setinputvalue(e.target.value)}  onKeyDown={x}></input>
          <FontAwesomeIcon icon={faPaperPlane} onClick={handle_send_message} />
          
        </div>
    </>
)
}
