import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import After from './after.jsx';
import {useState} from 'react'


export default function Front({socket}){
    const [name,setname]=useState('');
    const[id,setid]=useState('');
    const[hassearched,sethassearched]=useState(false);
   function x(e){
      let Name=document.querySelector('[id="name"]').value;
      let Id=document.querySelector('[id="id"]').value;
      setname(Name);
      setid(Id);
      sethassearched(true);
      
   }
   return (<>
   <div className="outer">
        <div className="front-box">
            <h2>Welcome in this app</h2>
            <input className="input" type="text" name="name" id="name" placeholder="Enter your name"></input>
            <input className="input" type="text" name="name" id="id" placeholder="Enter your id"></input>
            <button className="btn" type="submit" onClick={x}>Enter</button>
        </div>
        <After name={name} id={id} hassearched={hassearched}></After>
        </div>
    </>
   );
}
