import io from 'socket.io-client';
import React, { useState, useEffect } from 'react';
import ReactDom from 'react-dom';
import './index.css';

import { library } from '@fortawesome/fontawesome-svg-core';
import { fas } from '@fortawesome/free-solid-svg-icons';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons';

library.add(fas);
const socket = io('http://localhost:3000/');

export default function After(props) {
    const { name, id, hassearched } = props;

    if (!hassearched) {
        return null;
    }

    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState('');

    useEffect(() => {
        socket.emit('user-joined', name);
        socket.on('new-user-joined', (data) => {
            appendMessage(data, 'left');
        });
        socket.on('message-received', (data) => {
            appendMessage(data, 'left');
        });
    }, []);

    function handleSendMessage() {
        if (inputValue.trim() !== '') {
            let message = inputValue.trim();
            setMessages((prevMessages) => [...prevMessages, { message, position: 'right' }]);
            socket.emit('send-message', message);  // Emit the message to the server
            setInputValue('');
        }
    }

    function appendMessage(msg, position) {
        const newMessage = { message: msg, position: position };
        setMessages((prevMessages) => [...prevMessages, newMessage]);
    }

    function handleKeyDown(e) {
        e.preventDefault();
        if (e.key === 'Enter') {
            handleSendMessage();
        }
    }

    return (
        <>
            <div className="after">
                {messages.map((value, index) => (
                    <div className={`message ${value.position}`} key={index}>
                        <h2>{value.message}</h2>
                    </div>
                ))}
            </div>
            <div className="message-box">
                <input 
                    type="text" 
                    className="input-2" 
                    placeholder="Enter your message" 
                    value={inputValue} 
                    onChange={(e) => setInputValue(e.target.value)}  
                    onKeyDown={handleKeyDown} 
                />
                <FontAwesomeIcon icon={faPaperPlane} onClick={handleSendMessage} />
            </div>
        </>
    );
}
