import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons';

export default function After({ name, id, socket }) {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');

  useEffect(() => {
    socket.on('new-user-joined', (data) => {
      appendMessage(data.message, 'left');
    });
    socket.on('message-received', (data) => {
      appendMessage(data.message, 'left');
    });

  }, [socket]);

  function appendMessage(msg, position) {
    setMessages((prevMessages) => [...prevMessages, { message: msg, position: position }]);
  }

  function sendMessage() {
    if (inputValue.trim() !== '') {
      const message = inputValue.trim();
      setMessages((prevMessages) => [...prevMessages, { message: `You: ${message}`, position: 'right' }]);
      socket.emit('message', message);
      setInputValue('');
    }
  }

  function handleKeyDown(e) {
    if (e.key === 'Enter') {
      e.preventDefault();
      sendMessage();
    }
  }

  return (
    <>
      <div className="after">
        {messages.map((msg, index) => (
          <div className={`message ${msg.position}`} key={index}>
            <h2>{msg.message}</h2>
          </div>
        ))}
      </div>
      <div className="message-box">
        <input
          type="text"
          className="input-2"
          placeholder="Enter your message"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <FontAwesomeIcon icon={faPaperPlane} onClick={sendMessage} />
      </div>
    </>
  );
}
